    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
         NOMINEE_DETAILS nominee=new NOMINEE_DETAILS();
       try{
String sql = "INSERT INTO account_holder_info"
+"(bank_name,account_type,cust_name,cust_dob,cust_fathers_name,fathers_occupation,cust_mothers_name,mothers_occupation,cust_occupation,cust_mbno,cust_adhaar_no,cust_pancard_no,cust_state,cust_city,cust_district,cust_pincode,cust_houseno,cust_area,cust_streetaddress)"
+"VALUES (?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?)";
            Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3306/generalized_teller_machine","root","");
            PreparedStatement pst = con.prepareStatement(sql);
            

 
 
pst.setString(3,custname.getText());
pst.setString(4,custdob.getText());
pst.setString(5,custfathersname.getText());
pst.setString(6,custfathersoccupation.getText());
pst.setString(7,custmothersname.getText());
pst.setString(8,custmothersoccupation.getText());
pst.setString(9,custoccupation.getText());
pst.setString(10,custmobno.getText());
pst.setString(11,custadhaarno.getText());
pst.setString(12,custpancardno.getText());
//pst.setString(13,custstate.getText());
//pst.setString(14,custcity.getText());
pst.setString(15,custdistrict.getText());
pst.setString(16,custpincode.getText());
pst.setString(17,custhouseno.getText());
pst.setString(18,custarea.getText());
pst.setString(19,custstreetaddress.getText());

String val2=bankname.getSelectedItem().toString();
pst.setString(1, val2); 
String val3=accounttype.getSelectedItem().toString();
pst.setString(2, val3);
String val4=custstate.getSelectedItem().toString();
pst.setString(13, val4); 
String val5=custcity.getSelectedItem().toString();
pst.setString(14, val5);

          
pst.executeUpdate();    
JOptionPane.showMessageDialog(null, "inserted successfully");     
 
}
catch(SQLException | HeadlessException ex){
JOptionPane.showMessageDialog(null, ex);
} 
      
  
       

        nominee.setVisible(true);
        this.setVisible(false);
        
    }//GEN-LAST:event_jButton1ActionPerformed

